create table air_company (id bigint generated by default as identity, country_location varchar(255), name_company varchar(255), primary key (id));
create table air_plane (id bigint generated by default as identity, is_active boolean, number_seat_buisness integer, number_seat_lowcost integer, status varchar(255), type varchar(255), air_company_id bigint, primary key (id));
create table air_plane_flight_route (id bigint generated by default as identity, air_plane_id bigint, flight_route_id bigint, primary key (id));
create table bank_card (id bigint generated by default as identity, card_number bigint, user_profile_id bigint, primary key (id));
create table flight_route (id bigint generated by default as identity, distance integer, flight_date_end timestamp, flight_date_start timestamp, is_active boolean, price integer, route_end varchar(255), route_start varchar(255), primary key (id));
create table passenger_profile (id bigint generated by default as identity, lastname varchar(255), name varchar(255), passport_number varchar(255), user_profile_id bigint, primary key (id));
create table seat (id bigint generated by default as identity, is_ordered boolean, place varchar(255), type varchar(255), air_plane_id bigint, primary key (id));
create table status (id bigint generated by default as identity, status varchar(255), primary key (id));
create table user_order (id bigint generated by default as identity, flight_route_id bigint, status_id bigint, passenger_profile_id bigint, primary key (id));
create table user_profile (id bigint generated by default as identity, email varchar(255), is_blocket_profile boolean, lastname varchar(255), name varchar(255), phone integer, user_id bigint, primary key (id));